mapper.select:
SELECT id FROM user_info
WHERE type = 1
AND `name`=#{teacherName}

SELECT id FROM course
WHERE teacher_id = #{teacherId}
AND `name` = #{courseName}

SELECT id FROM user_info
WHERE type = 0
AND id = #{studentId}

mapper.delete:
DELETE FROM course_selection
WHERE class_id = #{classId}
AND student_id = #{userId}

